# Else and Elif in Python

# Simple if-else
print("--- If-Else ---")
age = 15

if age >= 18:
    print("Adult")
else:
    print("Minor")

# Multiple conditions with elif
print("\n--- Grade Calculator ---")
score = 78

if score >= 90:
    grade = "A"
elif score >= 80:
    grade = "B"
elif score >= 70:
    grade = "C"
elif score >= 60:
    grade = "D"
else:
    grade = "F"

print(f"Score: {score} -> Grade: {grade}")

# Multiple conditions example
print("\n--- Temperature Check ---")
temp = 65

if temp >= 85:
    print("Hot!")
elif temp >= 70:
    print("Warm")
elif temp >= 55:
    print("Cool")
else:
    print("Cold!")

# Ternary expression
print("\n--- Ternary Expression ---")
x = 10
result = "positive" if x > 0 else "non-positive"
print(f"{x} is {result}")

# Practical: Menu selection
print("\n--- Menu Demo ---")
choice = "2"

if choice == "1":
    print("New Game")
elif choice == "2":
    print("Load Game")
elif choice == "3":
    print("Settings")
else:
    print("Invalid option")
