# Python Calculator - Complete Implementation
import math

# Basic Operations
def add(a, b):
    """Return the sum of two numbers."""
    return a + b

def subtract(a, b):
    """Return the difference of two numbers."""
    return a - b

def multiply(a, b):
    """Return the product of two numbers."""
    return a * b

def divide(a, b):
    """Return the quotient of two numbers."""
    if b == 0:
        raise ValueError("Cannot divide by zero!")
    return a / b

def power(a, b):
    """Return a raised to the power of b."""
    return a ** b

def square_root(a, _=None):
    """Return the square root of a."""
    if a < 0:
        raise ValueError("Cannot calculate square root of negative number!")
    return math.sqrt(a)

# Input Handling
def get_number(prompt):
    """Prompt user for a number and validate input."""
    while True:
        try:
            return float(input(prompt))
        except ValueError:
            print("Invalid input. Please enter a valid number.")

# Menu System
def display_menu():
    """Display the calculator menu."""
    print("\n===== Python Calculator =====")
    print("1. Add")
    print("2. Subtract")
    print("3. Multiply")
    print("4. Divide")
    print("5. Power")
    print("6. Square Root")
    print("7. Exit")
    print("=============================")

def get_choice():
    """Get and validate menu choice."""
    while True:
        try:
            choice = int(input("Enter your choice (1-7): "))
            if 1 <= choice <= 7:
                return choice
            print("Please enter a number between 1 and 7.")
        except ValueError:
            print("Invalid input. Please enter a number.")

# Calculation Logic
def calculate(choice, num1, num2):
    """Perform the calculation based on user choice."""
    operations = {
        1: ("Addition", add),
        2: ("Subtraction", subtract),
        3: ("Multiplication", multiply),
        4: ("Division", divide),
        5: ("Power", power),
        6: ("Square Root", square_root)
    }

    name, func = operations[choice]
    result = func(num1, num2)
    return f"{name}: {result}"

# Main Program
def main():
    """Main program entry point."""
    print("Welcome to Python Calculator!")
    history = []

    while True:
        display_menu()
        choice = get_choice()

        if choice == 7:
            print("\nCalculation History:")
            for i, calc in enumerate(history, 1):
                print(f"  {i}. {calc}")
            print("\nThank you for using Python Calculator. Goodbye!")
            break

        num1 = get_number("Enter first number: ")
        num2 = None
        if choice != 6:  # Square root only needs one number
            num2 = get_number("Enter second number: ")

        try:
            result = calculate(choice, num1, num2)
            print(f"\nResult: {result}")
            history.append(result)
        except ValueError as e:
            print(f"\nError: {e}")

if __name__ == "__main__":
    main()
