from datetime import datetime

# Storing trades in a list of dictionaries
trades = [
    {"symbol": "AAPL", "price": 150.00, "volume": 10, "time": datetime(2023, 1, 1, 10, 0)},
    {"symbol": "GOOG", "price": 2800.00, "volume": 2, "time": datetime(2023, 1, 2, 11, 30)}
]

for trade in trades:
    print(f"Bought {trade['volume']} shares of {trade['symbol']} at ${trade['price']}")
